        charity = payable(0x314De8Cda9E6D0d50EC9e3303DA636Ce5adF28Fa); //Charity wallet address

        /*
         * Creating an instance of UniswapV2Router02
         * by passing router address inside Interface of UniswapV2Router02
         */
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(
            UNISWAPV2ROUTER
        );

        // Creating pair address
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());

        //Storing the instance of UniswapV2Router02 in a state variable
        uniswapV2Router = _uniswapV2Router;

        __Pausable_init_unchained();
        __Ownable_init_unchained();
        __Context_init_unchained();
    }

