    //This function executes when the contract balance >= `contractLimit`

    function swapAndLiquify(uint256 contractLPTokenBalance) private {
        uint256 halfOfBal = contractLPTokenBalance / 2; // Divides contract's LPToken Balance by Two
        uint256 otherHalf = contractLPTokenBalance - halfOfBal; // Calculating the other half

        // Calculating the initial balance of contract
        // This calculates the native balance of the contract
        uint256 contractInitialBal = address(this).balance;

        // calling {swapTokensForEth} function
        // passing the vale `halOfBal`
        // this function swaps the amount of tokens passed to equivalent Eth amount
        swapTokensForEth(halfOfBal);

        // getting the Eth value we got by calling swapTokensForEth function
        uint256 newContractBal = address(this).balance - contractInitialBal;

        // calling {addLiquidity} function
        // passing the arguements `otherHalf` and `newContractBal`
        // this function adds the liquidity with `otherHalf` amount of tokens and `newContractBal` amount of Eth
        addLiquidity(otherHalf, newContractBal);
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        address[] memory path = new address[](2); // creating memory space for path
        path[0] = address(this); // asssigning contract address to 0th index
        path[1] = uniswapV2Router.WETH(); // assigning wrappedEth address to 1st index

        // approving tokenAmount from contract address to router address
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // calling the function {swapExactTokensForETHSupportingFeeOnTransferTokens}
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(this),
            block.timestamp
        );
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmt) private {
        // approving tokenAmount from contract address to router address
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // calling the {addLiquidityETH} function from router instance
        uniswapV2Router.addLiquidityETH{value: ethAmt}(
            address(this),
            tokenAmount,
            0,
            0,
            owner(),
            block.timestamp
        );
    }

    /**
     * withdrawing the tokenAmount from the contract's token balance
     *
     *Requirements:
     *
     * caller must only be owner
     * `tokenAmount` must be less than or equal to `buySellTax`
     *
     * Emits {Tansfer} event
     *
     * deducting `tokenAmount` from the `buySellTax`
     */
    function withdraw(uint256 tokenAmount) public onlyOwner {
        require(
            tokenAmount <= buySellTax,
            "The amount entered is greater than balance"
        );
        _transfer(address(this), _msgSender(), tokenAmount);
        buySellTax -= tokenAmount;
    }

    // to receive native currency by the contract's address
    receive() external payable {}

    // only the owner can able to  upgrade the contract
    function _authorizeUpgrade(address) internal override onlyOwner {}

    // calculates and returns marketingfee
    function _calculateMarketingFee(uint256 amount)
        internal
        virtual
        returns (uint256)
    {
        return (amount * marketingFee) / 100;
    }

