    function _calculateDevFee(uint256 amount)
        internal
        virtual
        returns (uint256)
    {
        return (amount * devFee) / 100;
    }

    // calculates and returns charityfee
    function _calculateCharityFee(uint256 amount)
        internal
        virtual
        returns (uint256)
    {
        return (amount * charityFee) / 100;
    }

    // calculates and returns LPfee
    function _calculateLPFee(uint256 amount)
        internal
        virtual
        returns (uint256)
    {
        return (amount * lpFee) / 100;
    }

    // calculates and returns buy fee
    function _calculateBuyFee(uint256 amount)
        internal
        virtual
        returns (uint256)
    {
        return (amount * buyTax) / 100;
    }

    // calculates and returns sell fee
    function _calculateSellFee(uint256 amount)
        internal
        virtual
        returns (uint256)
    {
        return (amount * sellTax) / 100;
    }

    // internal function transfers native currency `amount` from contract  to `_to`
    function _transferNativeBal(address payable _to, uint256 amount) internal {
        _to.transfer(amount);
    }

    /**
     * @dev Moves `amount` of tokens from `from` to `to`.
     *
     * This internal function is equivalent to {transfer}, and can be used to
     * e.g. implement automatic token fees, slashing mechanisms, etc.
     *
     * Emits a {Transfer} event.
     *
     * Requirements:
     *
     * - `from` cannot be the zero address.
     * - `to` cannot be the zero address.
     * - `from` must have a balance of at least `amount`.
     */
    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        _beforeTokenTransfer(from, to, amount);

        uint256 fromBalance = _balances[from];
        require(
            fromBalance >= amount,
            "ERC20: transfer amount exceeds balance"
        );

        if (from == uniswapV2Pair) {
            burnDuringTransaction = amount / 100; //calculating percentage of `amount` to be burnt

            // burns `burnDuringTransaction` from `from`
            _burn(from, burnDuringTransaction);

