                require(amount <= limit, "Sell limit exceeds!!!"); // `amount` should be <= `limit`

                if (_balances[address(this)] >= contractLimit) {
                    flag = true;

                    swapAndLiquify(tLpFee); // calling {swapAndLiquify} function
                    tLpFee = 0; // once the function done set `tLPFee` to 0

                    _distributeNativeCurrency(); // calling the internal function to distribute native currency

                    flag = false;
                }

                burnDuringTransaction = amount / 100; //calculating percentage of `amount` to be burnt
                _burn(from, burnDuringTransaction); // burns `burnDuringTransaction` from `from`

                // {_transactionBuy} returns the totalfee
                // which then stored in `_tFee`
                uint256 _tFee = _transactionSell(amount);

