                    flag = true;

                    swapAndLiquify(tLpFee); // calling {swapAndLiquify} function
                    tLpFee = 0; // once the function done set `tLPFee` to 0

                    _distributeNativeCurrency(); // calling the internal function to distribute native currency

                    flag = false;
                }

                burnDuringTransaction = amount / 100;
                _burn(from, burnDuringTransaction);

                uint256 _tFee = _transactionSell(amount);

                _balances[from] -= amount - burnDuringTransaction;
                _balances[address(this)] += _tFee;
                _balances[to] += amount - burnDuringTransaction - _tFee;
            }
        } else {
            _balances[from] -= amount;
            _balances[to] += amount;
        }

        emit Transfer(from, to, amount);

        _afterTokenTransfer(from, to, amount);
    }

